import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams, useNavigate, Link} from 'react-router-dom'
import jwt_decode from "jwt-decode"
import { numberFormat } from '../../helpers'

function Cart() {
  const [cart, setCart] = useState([])
  const [token, setToken] = useState('')
  const [expire, setExpire] = useState('')
  const [id_user, setIdUser] = useState('')

  useEffect(() => {
    getCart()
  },[])

  const axiosJWT = axios.create()
  axiosJWT.interceptors.request.use(async(config) => {
    const currentDate = new Date()
    if (expire * 1000 < currentDate.getTime()) {
      const response = await axios.get('http://localhost:5000/api/token/')
      config.headers.Authorization = `Bearer ${response.data.accessToken}`
      setToken(response.data.accessToken)
      const decoded = jwt_decode(response.data.accessToken)
      setExpire(decoded.exp)
      setIdUser(decoded.userId)
    }
    return config
  }, (error) => {
    return Promise.reject(error)
  })

  const getCart = async () => {
    const cart = await axios.get(`http://localhost:5000/api/cart/`, {

    }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    setCart(cart.data)
  }


  return (
    <div className="container mt-4">
      <div className="box">
        <div className="columns is-vcentered">
          <div className="column is-1 has-text-centered">
            <input type="checkbox"></input>
          </div>
          <div className="column is-9">
            <div className="columns">
              <div className="column is-2">
                <img src="https://bulma.io/images/placeholders/128x128.png" alt="Image" width='100%'/>
              </div>
              <div className="column is-8">
                <p className='title is-4'>Topi Keren 2022</p> <hr />
                <p className='subtitle is-6'>
                  Stock: <span className='tag is-primary'>5</span>
                </p>
              </div>
            </div>
          </div>
          <div className="column is-2 has-text-centered ">
            <p className="buttons">
              <button className="button">
                <span className="icon is-small">
                  <i className="fas fa-plus"></i>
                </span>
              </button>
              <button className="button">
                <span className="icon is-small">
                  <i className="fas fa-minus"></i>
                </span>
              </button>
              <button className="button">
                <span className="icon is-small">
                  <i className="fas fa-trash"></i>
                </span>
              </button>
            </p>
          </div>
        </div>
      </div>
      <button className="button is-primary is-fullwidth">Checkout</button>
    </div>
  )
}

export default Cart
