import React, {useState, useEffect} from 'react'
import axios from 'axios'
import jwt_decode from "jwt-decode"
import {Link} from 'react-router-dom'

const ProductAdmin = () => {
  const [name, setName] = useState('')
  const [token, setToken] = useState('')
  const [expire, setExpire] = useState('')
  const [products, setProducts] = useState([])

  useEffect(()=>{
    getProducts()
  },[])

  const getProducts = async () => {
    const response = await axios.get("http://localhost:5000/api/product")
    setProducts(response.data)
  }

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/product/${id}`)
      getProducts()
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className="container mt-5">
      <Link to="/product/add" className="button is-success">
        Add New
      </Link>
      <div className="columns is-gapless mt-3">
        {products.map((product) => (
          <div className="column is-one-quarter mr-3" key={product._id}>
            <div className="card">
              <div className="card-image">
                <figure className="image is-4by3">
                  <img src={product.url} alt="Placeholder image"/>
                </figure>
              </div>
              <div className="card-content">
                <div className="media">
                  <div className="media-content">
                    <p className="title is-4">{product.title}</p>
                    <p className="subtitle is-6">@johnsmith</p>
                  </div>
                </div>
              </div>
              <footer className="card-footer">
                <Link to={`/product/edit/${product._id}`} className="card-footer-item">Edit</Link>
                <a onClick={()=>deleteProduct(product._id)} className="card-footer-item">Delete</a>
              </footer>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default ProductAdmin
