import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams, useNavigate, Link} from 'react-router-dom'
import jwt_decode from "jwt-decode"
import { numberFormat } from '../../helpers'

function OrderList() {
  const [order, setorder] = useState([])
  const [token, setToken] = useState('')
  const [expire, setExpire] = useState('')

  useEffect(() => {
    getOrder()
  },[])

  const axiosJWT = axios.create()
  axiosJWT.interceptors.request.use(async(config) => {
    const currentDate = new Date()
    if (expire * 1000 < currentDate.getTime()) {
      const response = await axios.get('http://localhost:5000/api/token/')
      config.headers.Authorization = `Bearer ${response.data.accessToken}`
      setToken(response.data.accessToken)
      const decoded = jwt_decode(response.data.accessToken)
      setExpire(decoded.exp)
    }
    return config
  }, (error) => {
    return Promise.reject(error)
  })

  const getOrder = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/token/')
      const decoded = await jwt_decode(response.data.accessToken)
      const carts = await axiosJWT.get(`http://localhost:5000/api/cart/${decoded.userId}`,{
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      setCart(carts.data)
      setIdUser(decoded.userId)
      getTotalHarga(decoded.userId)
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className="container my-3">
      <div className="box">
        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>Product</th>
              <th>Jumlah</th>
              <th>Total</th>
              <th>Pembeli</th>
              <th>Status</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th>1</th>
              <td>Topi JB-2022</td>
              <td>5</td>
              <td>150000</td>
              <td>Aditya</td>
              <td><span className='tag is-warning'>Pesan</span></td>
              <td>
                <div className="buttons">
                  <button className="button is-small is-success">Kirim</button>
                  <button className="button is-small is-danger">Tolak</button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
        
      </div>
    </div>
  );
}

export default OrderList
